import React from 'react';
import styled from 'styled-components';

const ImageWrapper = styled.section`
    width: 100%;
    height: 100vh;
    position: relative;
    overflow: hidden;

    .big-title{
    position: absolute;
    z-index: 30;
    line-height: 4rem;
    top: calc(50% - 2rem);
    width: 100%;
    text-align: center;
    font-size: 5rem;
    font-weight: 600;
    color: #fff;
}

    .person{
        position: absolute;
        width: 650px;
        bottom: 0px;
        left: -70px;
        z-index: 20;
    }

    .mountain1{
        position: absolute;
        bottom: -100px;
        width: 1500px;
        right: 0;
        z-index: 19;
    }
    .mountain2{
        position: absolute;
        width: 1100px;
        bottom: -100px;
        right: -499px;
        left: 0;
        z-index: 18;
    }
    .mountain3{
        position: absolute;
        width: 900px;
        bottom: 150px;
        right: 0;
        z-index: 17;
    }

    .sky{
    position: absolute;
    width: 2100px;
    bottom: 150px;
    right: 0;
}
`;

export default function HomeBackground() {
    return (
        <div>

        <ImageWrapper>

            <h1 className='big-title'>Tell everyone about the twists and turns in your journey</h1>


            <img src="img/person.png" alt="" className='person' dataSpeed='0.2'></img>
            <img src="img/mountain1.png" alt="" className='mountain1'></img>
            <img src="img/mountain2.png" alt="" className='mountain2'></img>
            <img src="img/mountain3.png" alt="" className='mountain3'></img>
            <img src="img/sky.png" alt="" className='sky'></img>




        </ImageWrapper>
        </div>

    )
}
