import React from 'react';
import styled from 'styled-components';

const ImageWrapper = styled.div`
    width: 100%;
    height: 100vh;
    position: relative;
    overflow: hidden;

    .big-title{
    position: absolute;
    z-index: 30;
    line-height: 4rem;
    top: calc(50% - 2rem);
    width: 100%;
    text-align: center;
    font-size: 5rem;
    font-weight: 600;
    color: #fff;
}

    .person{
        position: absolute;
        width: 650px;
        bottom: 0px;
        left: -70px;
        z-index: 20;
    }

    .mountain1{
        position: absolute;
        bottom: -100px;
        width: 1500px;
        right: 0;
        z-index: 19;
    }
    .mountain2{
        position: absolute;
        width: 1100px;
        bottom: -100px;
        right: -499px;
        left: 0;
        z-index: 18;
    }
    .mountain3{
        position: absolute;
        width: 900px;
        bottom: 150px;
        right: 0;
        z-index: 17;
    }

    .sky{
        position: absolute;
        width: 2100px;
        bottom: 150px;
        right: 0;
    }

    .shadowAbout{
        position: absolute;
        bottom: 100%;
        height: 300px;
        width: 100%;
        left: 0;
        z-index: 20;
        background: linear-gradient(to top, #151515, transparent);
    }
`;

export default function HomeBackground() {
    const translate = document.querySelectorAll(".translate");
    const big_title = document.querySelector(".big-title");
    const header = document.querySelector("#header");
    const shadow = document.querySelector(".shadow");
    // let header_height = header.offsetHeight;
    const section = document.querySelector("section");

    // let section_height = section.offsetHeight;


    window.addEventListener('scroll', () => {
        let scroll = window.pageYOffset;
        let sectionY = section.getBoundingClientRect();

        translate.forEach(element => {
            let speed = element.dataset.speed;
            element.style.transform = `translateY(${scroll * speed}px)`;
        });


        big_title.style.opacity = - scroll / (header_height / 2) + 1;
        shadow.style.height = `${scroll * 0.5 + 300}px`;

        // content.style.transform = `translateY(${scroll / (section_height + sectionY.top) * 50 - 50}px)`;

    })

    return (
        <div>
            <ImageWrapper id='header'>
                <h1 className='big-title'>Tell everyone about the twists and turns in your journey</h1>

                <img src="img/person.png" alt="" className='person' dataSpeed='0.2'></img>
                <img src="img/mountain1.png" alt="" className='mountain1 translate'></img>
                <img src="img/mountain2.png" alt="" className='mountain2 translate'></img>
                <img src="img/mountain3.png" alt="" className='mountain3 translate'></img>
                <img src="img/sky.png" alt="" className='sky'></img>
            </ImageWrapper>

            <div className='shadowAbout'></div>
            <section>
                <div className="shadow"></div>

                <div className="container">
                    <div className="content opacity">
                        <h3 className="title">
                            About
                            <div className="border"></div>
                        </h3>
                        <p className="text">Lorem ipsum dolor, sit amet consectetur adipisicing elit. Eaque officiis quos expedita ipsa, a quidem inventore voluptates debitis accusamus tenetur qui et voluptas dicta, culpa earum, doloribus odio consectetur consequuntur soluta quasi nobis! Deserunt voluptatum reiciendis iure expedita sequi quisquam laboriosam temporibus exercitationem.</p>
                    </div>

                    <div className="imgContainer opacity">
                    </div>
                </div>
            </section>
        </div>

    )
}
