    grid-template-rows: 1fr;
    align-items: center;

    .aboutUsContent{
        display: grid;
        align-self: center;
        padding-top: 5em;
        height: 110%;
        width: 100%;
        padding-left: 3em;
        background-color: black;
        color: #fff;

        .title{
            align-self: flex-end;
            position: relative;
