import React, {useEffect, useState} from 'react';

import styled from 'styled-components';
import Tilt from 'react-tilt';


export default function RecommendedPosts() {
    const [APIdata, setAPIData] = useState([]);

    const getUsers = async () => {
        let response = await (await fetch("https://randomuser.me/api/?results=3")).json()
        let results = response.results;
        setAPIData(results);

    }
    var u;

    useEffect(() => {

        getUsers();    
        console.log(APIdata);       
    }, [])






    return (
        <div>
        <RecommendedContainer>
            {/* <img src='/secondaryBg.jpg' alt='react-logo'></img> */}
            {APIdata[0] !== undefined ? 

            <div>
                <div className='title'>
                <img src={APIdata[0].picture.medium} alt='profile picture'></img>
                <h1>{APIdata[0].name.first} </h1>
                <p>{APIdata[0].location.city}, {APIdata[0].location.country}</p>
            </div>
            </div> : 'dejene'}

            <SeeMoreBtn>See More</SeeMoreBtn>            
        </RecommendedContainer>

        {/* <RecommendedContainer>
            <img src='/secondaryBg.jpg' alt='react-logo'></img>
            {APIdata[0] !== undefined ? <h1 style={{top: 0, bottom: '100%'}}>{APIdata[0].name.first}</h1> : 'dejene'}
            <SeeMoreBtn>See More</SeeMoreBtn>            
        </RecommendedContainer>  <RecommendedContainer>
            <img src='/secondaryBg.jpg' alt='react-logo'></img>
            {APIdata[0] !== undefined ? <h1 style={{top: 0, bottom: '100%'}}>{APIdata[0].name.first}</h1> : 'dejene'}
            <SeeMoreBtn>See More</SeeMoreBtn>            
        </RecommendedContainer> */}


        </div>
    )
}

const RecommendedContainer = styled(Tilt)`
    width: 80%;
    height: 25vh;;
    border: solid lightgrey 3px;
    border-radius: 3%;
    box-shadow: 5px 5px 5px 0  blanchedalmond;
    background-image: url('/secondaryBg.jpg');
    background-size: cover;
    display: grid;
    grid-template-columns: 1fr;
    padding: 0.5em;

    h1 {
        margin: 0;  
        height: fit-content;
        font-size: 12px;
        border: solid black 1px;
    }


   div {
       display: flex;
       column-gap: 1em;

       img {
           width: 3vw;
           height: 5vh;
           border-radius: 50%;
       }

       p {
           text-align: right;
           border: solid black;
           justify-self: center;    
       }
   }

    img {
        grid-column: span;
        width: 100%;
        height: 25vh;
        background-size: cover;
        background-position: center;

    }

    button {
        border: solid green 5px;    
        align-self: flex-end; 
        justify-self: center;   
    }
`;


const SeeMoreBtn = styled.button`
    width: 90%;
    height: 5vh;
    left: 5%;
    bottom: 5%;
    border-radius: 30px 30px 30px 30px;
    border: none;
    font-size: 16px;
    background: linear-gradient(45deg, magenta, snow 100%);
    &:hover {
        background-color: limegreen;
        color: slateblue;
        font-weight: 900;
        border: orangered 3px solid;
    }
`;
