export default function Greeting() {

    const [greetingName, setGreetingName] = useState('');

    useEffect(() => {
        let name = ""
        const nameGreeting = async () => {
            name = await (await fetch('/name')).json();
            setGreetingName(name.firstName);
        }
        nameGreeting();
        console.log('this is my name greeting: ', greetingName);


        fetch("https://random-user.p.rapidapi.com/getuser", {
	"method": "GET",
	"headers": {
		"x-rapidapi-key": "051b0d4b2dmsh95d0aa18a755171p1201f2jsn9f84f4a7f2b4",
		"x-rapidapi-host": "random-user.p.rapidapi.com"
	}
})
.then(response => {
	console.log(response);
})
.catch(err => {
	console.error(err);
});
    })
    return (
        <div>
            <h1>Your feed</h1>
            <h2>What will you tell the world today  {greetingName}?</h2>

        </div>
    )
}
