import React, {useEffect, useState} from 'react';
import styled from 'styled-components';
import Tilt from 'react-tilt';


export default function RecommendedPosts() {
    const [APIdata, setAPIData] = useState([]);

    const getUsers = async () => {
        let response = await (await fetch("https://randomuser.me/api/?results=3")).json()
        console.log(response);
        setAPIData(response.results.results);

    }

    useEffect(() => {

        getUsers();


    }, [])






    return (
        <RecommendedContainer>
            <img src='/secondaryBg.jpg' alt='react-logo'></img>
            {APIdata === null ? null : APIdata.map(user => <h1>{user.name.first}</h1>)}
            {/* <SeeMoreBtn>See More</SeeMoreBtn>             */}
        </RecommendedContainer>
    )
}

const RecommendedContainer = styled(Tilt)`
    width: 80%;
    height: fit-content;
    border: solid lightgrey 3px;
    border-radius: 3%;
    box-shadow: 5px 5px 5px 0  darkorange;
    /* background-image: url('/seeMoreBg.jpg');
    background-size: cover;
    filter: blur(10px);
    -webkit-filter: blur(5px); */

    img {
        width: 100%;
        height: 25vh;
        background-size: cover;
        filter: blur(px);
        -webkit-filter: blur(10px); 
    }
`;


const SeeMoreBtn = styled.button`
    width: 90%;
    height: 5vh;
    position: absolute;
    left: 5%;
    bottom: 5%;
    border-radius: 30px 30px 30px 30px;
    font-size: 16px;

    background: linear-gradient(45deg, magenta, snow 100%);
    &:hover {
        background-color: limegreen;
        color: slateblue;
        font-weight: 900;
    }
`;
